import React from 'react';
import '../../css/Style.css';
import { Formik, Form, FieldArray, useField } from 'formik';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const initialValues = {
    produits: [
        {
            code: '',
            descrption: '',
            qte_demandee: '',
            prix: ''
        },
    ],
};

const MyTextField = ({ placeholder, ...props }) => {
    const [field, meta] = useField(props);
    const error = meta.error && meta.touched ? meta.error : '';
    return (
        <TextField
            className="product-form-item"
            placeholder={placeholder}
            {...field}
            helperText={error}
            error={!!error}
        />
    );
}

/**
 * @author Alex Lajeunesse
 * @function CréerProduits
 * @description Gère la section d'ajout de produits (affichage, requêtes à l'API, redirection et formulaires)
 * @see https://formik.org/docs/examples/field-arrays
 */
export default function CreerProduits() {
    return (
        <div>
            <h1>Produits</h1>
            <Paper>
                <Formik
                    initialValues={initialValues}
                    onSubmit={async (values) => {
                        await new Promise((r) => setTimeout(r, 500));
                        alert(JSON.stringify(values, null, 2));
                    }}
                >
                    {({ values, isSubmitting, handleChange, handleBlur, handleSubmit, handleNewField }) => (
                        <Form>
                            <FieldArray name="produits">
                                {({ insert, remove, push }) => (
                                    <div>
                                        {values.produits.length > 0 &&
                                            values.produits.map((friend, index) => (
                                                <div className="row" key={index}>
                                                    <div className="col">
                                                        <MyTextField
                                                            name={`produits.${index}.qte_demandee`}
                                                            placeholder="Quantité demandée"
                                                            type="text"
                                                        />
                                                        <MyTextField
                                                            name={`produits.${index}.code`}
                                                            placeholder="Code"
                                                            type="text"
                                                        />
                                                        <MyTextField
                                                            name={`produits.${index}.descrption`}
                                                            placeholder="Description"
                                                            type="text"
                                                        />
                                                        <MyTextField
                                                            name={`produits.${index}.prix`}
                                                            placeholder="Prix"
                                                            type="text"
                                                        />
                                                        <Button className="form-button" disabled={isSubmitting} onClick={() => remove(index)}><b>X</b></Button>
                                                    </div>
                                                    <div className="col">
                                                    </div>
                                                </div>
                                            ))}
                                        <Button className="form-button" disabled={isSubmitting}
                                            onClick={() => push({ code: '', descrption: '', qte_demandee: '', prix: '' })}
                                        ><b>Ajouter un produit</b></Button>
                                    </div>
                                )}
                            </FieldArray>
                            <Button className="form-button" disabled={isSubmitting} type="submit"><b>Enregistrer</b></Button>
                        </Form>
                    )}
                </Formik>
            </Paper>
        </div>
    );
}
