import React from 'react';
import '../css/Style.css';
import axios from 'axios';
import NavBar from './rfc/NavBar';
import TabCommandes from './rfc/TabCommandes';
// Composants Material-UI
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import IconButton from '@material-ui/core/IconButton';
// Material-UI Icons
import AssignmentIcon from '@material-ui/icons/Assignment';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
// react-router-dom
import { Link } from 'react-router-dom';

class Commandes extends React.Component {
  constructor(props) {
    super();
    this.handleEdit = this.handleEdit.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  componentDidMount() {
    this.getCommandes();
  }

  state = {
    commandes: []
  }

  async getCommandes() {
    try {
      const response = await axios.get('http://localhost:5000/api/commandes/');
      console.log(response);
      // La réponse de l'API est enregistré dans le state
      this.setState({
        commandes: response.data
      });
    } catch (error) {
      console.error(error);
    }
  }

  handleEdit(event) {
    event.preventDefault();
    console.log("2");
  }

  handleDelete(event) {
    event.preventDefault();
    console.log("3");
  }

  render() {
    return (
      <div>
        <header>
          <NavBar />
        </header>
        <div className="content">
          <h1>Bienvenue !</h1>
          <TabCommandes />
        </div>
      </div>
    );
  }
}

export default Commandes;
