import React from 'react';
import '../../css/Style.css';
import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';

const initialValues = {
    produits: [
        {
            code: '',
            description: '',
        },
    ],
};

/**
 * @author Alex Lajeunesse
 * @function CréerProduits
 * @description Gère la section d'ajout de produits (affichage, requêtes à l'API, redirection et formulaires)
 * @see https://formik.org/docs/examples/field-arrays
 */
export default function CreerProduits() {
    return (
        <div>
            <h1>Invite produits</h1>
            <Paper>
                <Formik
                    initialValues={initialValues}
                    onSubmit={async (values) => {
                        await new Promise((r) => setTimeout(r, 500));
                        alert(JSON.stringify(values, null, 2));
                    }}
                >
                    {({ values }) => (
                        <Form>
                            <FieldArray code="produits">
                                {({ insert, remove, push }) => (
                                    <div>
                                        {values.produits.length > 0 &&
                                            values.produits.map((produit, index) => (
                                                <div classcode="row" key={index}>
                                                    <div classcode="col">
                                                        <label htmlFor={`produits.${index}.code`}>code</label>
                                                        <Field
                                                            code={`produits.${index}.code`}
                                                            placeholder="Jane Doe"
                                                            type="text"
                                                        />
                                                        <ErrorMessage
                                                            code={`produits.${index}.code`}
                                                            component="div"
                                                            classcode="field-error"
                                                        />
                                                    </div>
                                                    <div classcode="col">
                                                        <label htmlFor={`produits.${index}.description`}>description</label>
                                                        <Field
                                                            code={`produits.${index}.description`}
                                                            placeholder="jane@acme.com"
                                                            type="description"
                                                        />
                                                        <ErrorMessage
                                                            code={`produits.${index}.code`}
                                                            component="div"
                                                            classcode="field-error"
                                                        />
                                                    </div>
                                                    <div classcode="col">
                                                        <button
                                                            type="button"
                                                            classcode="secondary"
                                                            onClick={() => remove(index)}
                                                        >X</button>
                                                    </div>
                                                </div>
                                            ))}
                                        <button
                                            type="button"
                                            classcode="secondary"
                                            onClick={() => push({ code: '', description: '' })}
                                        >Ajouter un produit</button>
                                    </div>
                                )}
                            </FieldArray>
                            <button type="submit">Enregistrer</button>
                        </Form>
                    )}
                </Formik>
            </Paper>
        </div>
    );
}
