import React from 'react';
import '../../css/Style.css';
import { Formik, Form, FieldArray, useField, ErrorMessage } from 'formik';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';

const initialValues = {
    produits: [
        {
            code: '',
            descrption: '',
        },
    ],
};

const MyTextField = ({ placeholder, ...props }) => {
    const [field, meta] = useField(props);
    const error = meta.error && meta.touched ? meta.error : '';
    return (
        <TextField
            className="form-item"
            placeholder={placeholder}
            {...field}
            helperText={error}
            error={!!error}
        />
    );
}

/**
 * @author Alex Lajeunesse
 * @function CréerProduits
 * @description Gère la section d'ajout de produits (affichage, requêtes à l'API, redirection et formulaires)
 * @see https://formik.org/docs/examples/field-arrays
 */
export default function CreerProduits() {
    return (
        <div>
            <h1>Invite produits</h1>
            <Paper>
                <Formik
                    initialValues={initialValues}
                    onSubmit={async (values) => {
                        await new Promise((r) => setTimeout(r, 500));
                        alert(JSON.stringify(values, null, 2));
                    }}
                >
                    {({ values }) => (
                        <Form>
                            <FieldArray name="produits">
                                {({ insert, remove, push }) => (
                                    <div>
                                        {values.produits.length > 0 &&
                                            values.produits.map((friend, index) => (
                                                <div className="row" key={index}>
                                                    <div className="col">
                                                        <MyTextField
                                                            name={`produits.${index}.code`}
                                                            placeholder="Jane Doe"
                                                            type="text"
                                                        />
                                                        <ErrorMessage
                                                            name={`produits.${index}.code`}
                                                            component="div"
                                                            className="field-error"
                                                        />
                                                        <MyTextField
                                                            name={`produits.${index}.descrption`}
                                                            placeholder="jane@acme.com"
                                                            type="descrption"
                                                        />
                                                        <ErrorMessage
                                                            name={`produits.${index}.code`}
                                                            component="div"
                                                            className="field-error"
                                                        />
                                                    </div>
                                                    <div className="col">
                                                        <button
                                                            type="button"
                                                            className="secondary"
                                                            onClick={() => remove(index)}
                                                        >X</button>
                                                    </div>
                                                </div>
                                            ))}
                                        <button
                                            type="button"
                                            className="secondary"
                                            onClick={() => push({ code: '', descrption: '' })}
                                        >Add Friend</button>
                                    </div>
                                )}
                            </FieldArray>
                            <button type="submit">Invite</button>
                        </Form>
                    )}
                </Formik>
            </Paper>
        </div>
    );
}
