import React from 'react'
import '../css/Style.css';
import NavBar from './rfc/NavBar';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

/**
 * @author Alex Lajeunesse
 * @class CreationCommande
 * @description Gère la page de création et de modification de commandes (affichage, requêtes à l'API, redirection et formulaires)
 */
export default class CreationCommande extends React.Component {

    constructor(props) {
        super();

        // Localisation en français
        Yup.setLocale({
            mixed: {
                default: 'Champ ${path} invalide.',
                required: 'Le champ ${path} est obligatoire'
            },
            string: {
                max: 'Le champ ${path} doit contenir un maximum de ${max] caractères.',
                email: 'L\'adresse courriel doit être valide'
            }
        });

        const schema = Yup.object({
            entreprise: Yup.string()
                .max(64),

            nom: Yup.string()
                .max(64),

            no_compte: Yup.number()
                .max(169999999),

            telephone: Yup.string()
                .max(32),

            courriel: Yup.string()
                .email()
                .max(255),

            po_client: Yup.string()
                .max(64),

            vendeur: Yup.string()
                .max(4)
                .required(),

            commentaire: Yup.string()
                .max(512),

            attention: Yup.string()
                .email()
                .max(255)
                .required()
        });
    }

    /**
     * @author Alex Lajeunesse
     * @function render
     * @description Affiche l'interface.
     * @see https://www.youtube.com/watch?v=FD50LPJ6bjE
     */
    render() {
        return (
            <div>
                <header>
                    <NavBar />
                </header>
                <div className="content">
                    <Paper>
                        <Formik
                            initialValues={{ entreprise: 'Cascades' }}
                            onSubmit={(data, { setSubmitting }) => {
                                setSubmitting(true);
                                console.log(data);
                                setSubmitting(false);
                            }}
                        >
                            {({ values, isSubmitting, handleChange, handleBlur, handleSubmit }) => (
                                <Form>
                                    <div>
                                        <Field className="form-item"
                                            placeholder="Nom d'entreprise"
                                            name="entreprise"
                                            type="input"
                                            as={TextField}
                                        />
                                        <Field className="form-item"
                                            placeholder="Numéro de compte"
                                            name="no_compte"
                                            type="input"
                                            as={TextField}
                                        />
                                        <Field className="form-item"
                                            placeholder="Nom du client"
                                            name="nom"
                                            type="input"
                                            as={TextField}
                                        />
                                    </div>
                                    <div>
                                        <Field className="form-item"
                                            placeholder="Téléphone"
                                            name="telephone"
                                            type="input"
                                            as={TextField}
                                        />
                                        <Field className="form-item"
                                            placeholder="Courriel"
                                            name="courriel"
                                            type="input"
                                            as={TextField}
                                        />
                                        <Field className="form-item"
                                            placeholder="PO du client"
                                            name="po_client"
                                            type="input"
                                            as={TextField}
                                        />
                                    </div>
                                    <div>
                                        <Field className="form-item"
                                            placeholder="Vendeur"
                                            name="vendeur"
                                            type="input"
                                            as={TextField}
                                        />
                                        <Field className="form-item"
                                            placeholder="Attention"
                                            name="attention"
                                            type="input"
                                            as={TextField}
                                        />
                                    </div>
                                    <div>
                                        <Field className="form-item"
                                            placeholder="Commentaire"
                                            name="commentaire"
                                            type="input"
                                            as={TextField}
                                        />
                                    </div>
                                    <div>
                                        <Button className="form-item" disabled={isSubmitting} type="submit"><b>Enregistrer</b></Button>
                                    </div>
                                    <pre>{JSON.stringify(values, null, 2)}</pre>
                                </Form>
                            )}
                        </Formik>
                    </Paper>
                </div>
            </div>
        )
    }
}
