import React from 'react';
import '../../css/Style.css';
import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';

const initialValues = {
    friends: [
        {
            name: '',
            email: '',
        },
    ],
};

/**
 * @author Alex Lajeunesse
 * @function CréerProduits
 * @description Gère la section d'ajout de produits (affichage, requêtes à l'API, redirection et formulaires)
 * @see https://formik.org/docs/examples/field-arrays
 */
export default function CréerProduits() {
    return (
        <div>
            <h1>Invite friends</h1>
            <Paper>
                <Formik
                    initialValues={initialValues}
                    onSubmit={async (values) => {
                        await new Promise((r) => setTimeout(r, 500));
                        alert(JSON.stringify(values, null, 2));
                    }}
                >
                    {({ values }) => (
                        <Form>
                            <FieldArray name="friends">
                                {({ insert, remove, push }) => (
                                    <div>
                                        {values.friends.length > 0 &&
                                            values.friends.map((friend, index) => (
                                                <div className="row" key={index}>
                                                    <div className="col">
                                                        <label htmlFor={`friends.${index}.name`}>Name</label>
                                                        <Field
                                                            name={`friends.${index}.name`}
                                                            placeholder="Jane Doe"
                                                            type="text"
                                                        />
                                                        <ErrorMessage
                                                            name={`friends.${index}.name`}
                                                            component="div"
                                                            className="field-error"
                                                        />
                                                    </div>
                                                    <div className="col">
                                                        <label htmlFor={`friends.${index}.email`}>Email</label>
                                                        <Field
                                                            name={`friends.${index}.email`}
                                                            placeholder="jane@acme.com"
                                                            type="email"
                                                        />
                                                        <ErrorMessage
                                                            name={`friends.${index}.name`}
                                                            component="div"
                                                            className="field-error"
                                                        />
                                                    </div>
                                                    <div className="col">
                                                        <button
                                                            type="button"
                                                            className="secondary"
                                                            onClick={() => remove(index)}
                                                        >X</button>
                                                    </div>
                                                </div>
                                            ))}
                                        <button
                                            type="button"
                                            className="secondary"
                                            onClick={() => push({ name: '', email: '' })}
                                        >Add Friend</button>
                                    </div>
                                )}
                            </FieldArray>
                            <button type="submit">Invite</button>
                        </Form>
                    )}
                </Formik>
            </Paper>
        </div>
    );
}
