import React from 'react'
import '../css/Style.css';
import NavBar from './rfc/NavBar';
import { Formik } from 'formik';
// Composants Material-UI
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

/**
 * @author Alex Lajeunesse
 * @class CreationCommande
 * @description Gère la page de création et de modification de commandes (affichage, requêtes à l'API, redirection et formulaires)
 */
export default class CreationCommande extends React.Component {

    /**
     * @author Alex Lajeunesse
     * @function render
     * @description Affiche l'interface.
     * @see https://www.youtube.com/watch?v=FD50LPJ6bjE
     */
    render() {
        return (
            <div>
                <header>
                    <NavBar />
                </header>
                <div className="content">
                    <Paper>
                        <Formik
                            initialValues={{ entreprise: 'Cascades' }}
                            onSubmit={(data, {setSubmitting}) => {
                                setSubmitting(true);
                                console.log(data);
                                setSubmitting(false);
                            }}
                        >
                            {({ values, isSubmitting, handleChange, handleBlur, handleSubmit }) => (
                                <form onSubmit={handleSubmit}>
                                    <TextField className="form-input"
                                        name="entreprise"
                                        value={values.entreprise}
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                    />
                                    <div>
                                        <Button disabled={isSubmitting} type="submit">Enregistrer</Button>
                                    </div>
                                    <pre>{JSON.stringify(values, null, 2)}</pre>
                                </form>
                            )}
                        </Formik>
                    </Paper>
                </div>
            </div>
        )
    }
}
